import "./App.css"
import React, { useState, useEffect, useRef } from "react"
import productsData from "./productsData.json"

function App() {
  const shoesProducts = productsData.filter(
    (product) => product.category === "Shoes"
  )
  const watchesProducts = productsData.filter(
    (product) => product.category === "Watches"
  )

  const [displayShoes, setDisplayShoes] = useState(true)
  const [displayWatches, setDisplayWatches] = useState(false)
  const [searchTerm, setSearchTerm] = useState("")
  const [showCartPopup, setShowCartPopup] = useState(false)
  const [cartProduct, setCartProduct] = useState(null)

  const handleDisplayShoes = () => {
    setDisplayWatches(false)
    setDisplayShoes(true)
  }

  const handleDisplayWatches = () => {
    setDisplayShoes(false)
    setDisplayWatches(true)
  }

  const handleSearchItem = (event) => {
    const searchValue = event.target.value
    setSearchTerm(searchValue)
  }

  const filteredShoesProducts = shoesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const filteredWatchesProducts = watchesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const handleCartClick = () => {
    setShowCartPopup(!showCartPopup)
  }

  const cartPopupRef = useRef(null)

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        cartPopupRef.current &&
        !cartPopupRef.current.contains(event.target)
      ) {
        setShowCartPopup(false)
      }
    }

    document.addEventListener("mousedown", handleClickOutside)

    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [])

  const handleCart = (id, image, name, price) => {
    setCartProduct({ id, image, name, price })
  }

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input
          id="searchItem"
          placeholder="Search Product"
          onChange={handleSearchItem}
        ></input>
        <div id="cart-logo" onClick={handleCartClick}>
          Cart
        </div>

        {showCartPopup && (
          <div ref={cartPopupRef} id="cart-popup" className="cart-popup">
            <div className="cart-content">
              {cartProduct ? (
                <>
                  <div className="cartDetails">
                    <img
                      id="cartProductImg"
                      src={cartProduct.image}
                      alt={cartProduct.name}
                    />
                    <p id="cartProductName">{cartProduct.name}</p>
                    <p id="cartProductPrice">{cartProduct.price}</p>
                  </div>
                </>
              ) : (
                <p>No product selected.</p>
              )}
              <button id="closeCartbtn" onClick={handleCartClick}>
                Close
              </button>
            </div>
          </div>
        )}
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button
              id="shoesBtn"
              className={displayShoes ? "shoesActive" : ""}
              onClick={handleDisplayShoes}
            >
              Shoes
            </button>
            <button
              id="watchesBtn"
              className={displayWatches ? "watchesActive" : ""}
              onClick={handleDisplayWatches}
            >
              Watches
            </button>
          </div>

          {displayShoes && (
            <div className="shoes">
              {filteredShoesProducts.length > 0 ? (
                filteredShoesProducts.map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">{product.price}</p>
                    <button
                      id="addToCartBtn"
                      onClick={() =>
                        handleCart(
                          product.id,
                          product.image,
                          product.name,
                          product.price
                        )
                      }
                    >
                      Add to Cart
                    </button>
                  </div>
                ))
              ) : (
                <p id="noResultsFound">No results found in shoes.</p>
              )}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {filteredWatchesProducts.length > 0 ? (
                filteredWatchesProducts.map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">{product.price}</p>
                    <button id="addToCartBtn">Add to Cart</button>
                  </div>
                ))
              ) : (
                <p id="noResultsFound">No results found in watches.</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default App
