import './App.css';
import React from 'react';
import productsData from './productsData.json'; // Import the products data

function App() {
  const shoesProducts = productsData.filter(product => product.category === 'Shoes');
  const watchesProducts = productsData.filter(product => product.category === 'Watches');

  return (
    <div className="app">
      <div className='header'>
        <p id='logo'>ShopCart</p>
        <input id='searchItem' placeholder='Search Item'></input>
        <p id='cart-logo'>Cart</p>
      </div>
      <div className='container'>
        <div className='offerSection'></div>
        <div className='shoppingItem'>
          <div className='Shoes'>
            <button id='shoesBtn'>Shoes</button>
            {shoesProducts.map(product => (
              <div key={product.id}>
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.price}</p>
                <p>{product.description}</p>
              </div>
            ))}
          </div>
          <div className='Watches'>
          <button id='watchesBtn'>Watches</button>

            {watchesProducts.map(product => (
              <div key={product.id}>
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.price}</p>
                <p>{product.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
