import "./App.css"
import React, { useState } from "react"
import productsData from "./productsData.json"

function App() {
  let [displayShoes, setDisplayShoes] = useState(true)
  let [displayWatches, setDisplayWatches] = useState(false)

  const allProducts = productsData;
  const [displayProducts, setDisplayProducts] = useState(allProducts);

  const shoesProducts = productsData.filter(
    (product) => product.category === "Shoes"
  )
  const watchesProducts = productsData.filter(
    (product) => product.category === "Watches"
  )

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
    setDisplayProducts(allProducts.filter((product) => product.category === "Shoes"));
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
    setDisplayProducts(allProducts.filter((product) => product.category === "Watches"));
  };

  //displayProducts

  const handleSearchItem = (event) => {
    const searchTerm = event.target.value.toLowerCase();
    setDisplayProducts(
      allProducts.filter((product) =>
        product.name.toLowerCase().includes(searchTerm)
      )
    );
  };

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input id="searchItem" placeholder="Search Item" onChange={handleSearchItem}></input>
        <p id="cart-logo">Cart</p>
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button id="shoesBtn" onClick={handleDisplayShoes}>
              Shoes
            </button>
            <button id="watchesBtn" onClick={handleDisplayWatches}>
              Watches
            </button>
          </div>

          {displayShoes && (
            <div className="shoes">
              {shoesProducts.map((product) => (
                <div className="productDesc" key={product.id}>
                  <img id="productImg" src={product.image} alt={product.name} />
                  <h3>{product.name}</h3>
                  <p id="itemPrice">{product.price}</p>
                  <button id="addToCartBtn">Add to Cart</button>
                </div>
              ))}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {watchesProducts.map((product) => (
                <div className="productDesc" key={product.id}>
                  <img id="productImg" src={product.image} alt={product.name} />
                  <h3>{product.name}</h3>
                  <p id="itemPrice">{product.price}</p>
                  <button id="addToCartBtn">Add to Cart</button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default App
