import "./App.css";
import React, { useState, useEffect, useRef } from "react";
import productsData from "./productsData.json";

function App() {
  const shoesProducts = productsData.filter(
    (product) => product.category === "Shoes"
  );
  const watchesProducts = productsData.filter(
    (product) => product.category === "Watches"
  );

  const [displayShoes, setDisplayShoes] = useState(true);
  const [displayWatches, setDisplayWatches] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [cartProducts, setCartProducts] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
  };

  const handleSearchItem = (event) => {
    const searchValue = event.target.value;
    setSearchTerm(searchValue);
  };

  const filteredShoesProducts = shoesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredWatchesProducts = watchesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCartClick = () => {
    setShowCartPopup(!showCartPopup);
  };

  const cartPopupRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        cartPopupRef.current &&
        !cartPopupRef.current.contains(event.target)
      ) {
        setShowCartPopup(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleCart = (id, image, name, price, category) => {
    const newCartItem = { id, image, name, price, category };
    setCartProducts((prevCartProducts) => [...prevCartProducts, newCartItem]);
  };

  useEffect(() => {
    // Calculate the total cost whenever cartProducts changes
    const totalCost = cartProducts.reduce((total, product) => total + Number(product.price), 0);
    // Update the totalCost state
    setTotalCost(totalCost);
  }, [cartProducts]);

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input
          id="searchItem"
          placeholder="Search Product"
          onChange={handleSearchItem}
        ></input>
        <div id="cart-logo" onClick={handleCartClick}>
          Cart
        </div>

        {showCartPopup && (
          <div ref={cartPopupRef} id="cart-popup" className="cart-popup">
            <div className="cart-content">
              {cartProducts.length > 0 ? (
                cartProducts.map((product) => (
                  <div key={product.id} className="cartDetails">
                    <img
                      id="cartProductImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <p id="cartProductName">{product.name}</p>
                    <p id="quantity">1</p>
                    <p id="cartProductPrice">Rs{product.price}</p>
                  </div>
                ))
              ) : (
                <p>No products in the cart.</p>
              )}

              {cartProducts.length > 0 && (
                <div className="totalPrice">
                  <h3>Total Price: {totalCost}</h3>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button
              id="shoesBtn"
              className={displayShoes ? "shoesActive" : ""}
              onClick={handleDisplayShoes}
            >
              Shoes
            </button>
            <button
              id="watchesBtn"
              className={displayWatches ? "watchesActive" : ""}
              onClick={handleDisplayWatches}
            >
              Watches
            </button>
          </div>

          {displayShoes && (
            <div className="shoes">
              {filteredShoesProducts.map((product) => (
                <div className="productDesc" key={product.id}>
                  <img
                    id="productImg"
                    src={product.image}
                    alt={product.name}
                  />
                  <h3>{product.name}</h3>
                  <p id="itemPrice">Rs{product.price}</p>
                  <button
                    id="addToCartBtn"
                    onClick={() =>
                      handleCart(
                        product.id,
                        product.image,
                        product.name,
                        product.price,
                        "Shoes"
                      )
                    }
                  >
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {filteredWatchesProducts.map((product) => (
                <div className="productDesc" key={product.id}>
                  <img
                    id="productImg"
                    src={product.image}
                    alt={product.name}
                  />
                  <h3>{product.name}</h3>
                  <p id="itemPrice">Rs{product.price}</p>
                  <button
                    id="addToCartBtn"
                    onClick={() =>
                      handleCart(
                        product.id,
                        product.image,
                        product.name,
                        product.price,
                        "Watches"
                      )
                    }
                  >
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
