import "./App.css";
import React, { useState } from "react";
import productsData from "./productsData.json";

function App() {
  const shoesProducts = productsData.filter((product) => product.category === "Shoes");
  const watchesProducts = productsData.filter((product) => product.category === "Watches");

  const [displayShoes, setDisplayShoes] = useState(true);
  const [displayWatches, setDisplayWatches] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
  };

  const handleSearchItem = (event) => {
    const searchValue = event.target.value;
    setSearchTerm(searchValue);
  };

  const filteredShoesProducts = shoesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredWatchesProducts = watchesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input id="searchItem" placeholder="Search Item" onChange={handleSearchItem}></input>
        <p id="cart-logo">Cart</p>
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button id="shoesBtn" onClick={handleDisplayShoes}>
              Shoes
            </button>
            <button id="watchesBtn" onClick={handleDisplayWatches}>
              Watches
            </button>
          </div>

          {displayShoes && (
            <div className="shoes">
              {filteredShoesProducts.length > 0 ? (
                filteredShoesProducts.map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img id="productImg" src={product.image} alt={product.name} />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">{product.price}</p>
                    <button id="addToCartBtn">Add to Cart</button>
                  </div>
                ))
              ) : (
                <p id='noResultsFound'>No results found in shoes section.</p>
              )}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {filteredWatchesProducts.length > 0 ? (
                filteredWatchesProducts.map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img id="productImg" src={product.image} alt={product.name} />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">{product.price}</p>
                    <button id="addToCartBtn">Add to Cart</button>
                  </div>
                ))
              ) : (
                <p id='noResultsFound'>No results found in watches section.</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
