import "./App.css";
import React, { useState, useEffect, useRef } from "react";
import productsData from "./productsData.json";

function App() {
  const shoesProducts = productsData.filter(
    (product) => product.category === "Shoes"
  );
  const watchesProducts = productsData.filter(
    (product) => product.category === "Watches"
  );

  const [displayShoes, setDisplayShoes] = useState(true);
  const [displayWatches, setDisplayWatches] = useState(false);
  const [searchTermShoes, setSearchTermShoes] = useState(""); // Separate search term for shoes
  const [searchTermWatches, setSearchTermWatches] = useState(""); // Separate search term for watches
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [cartProducts, setCartProducts] = useState([]);

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
  };

  const handleSearchItemShoes = (event) => {
    const searchValue = event.target.value;
    setSearchTermShoes(searchValue);
  };

  const handleSearchItemWatches = (event) => {
    const searchValue = event.target.value;
    setSearchTermWatches(searchValue);
  };

  const filteredShoesProducts = shoesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTermShoes.toLowerCase())
  );

  const filteredWatchesProducts = watchesProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTermWatches.toLowerCase())
  );

  const handleCartClick = () => {
    setShowCartPopup(!showCartPopup);
  };

  const cartPopupRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        cartPopupRef.current &&
        !cartPopupRef.current.contains(event.target)
      ) {
        setShowCartPopup(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleCart = (id, image, name, price, category) => {
    const newCartItem = { id, image, name, price, category };
    setCartProducts((prevCartProducts) => [...prevCartProducts, newCartItem]);
  };

  useEffect(() => {
    // Calculate the total cost whenever cartProducts changes
    const totalCost = cartProducts.reduce((total, product) => total + Number(product.price), 0);
    // Update the totalCost state
    setTotalCost(totalCost);
  }, [cartProducts]);

  const [totalCost, setTotalCost] = useState(0); // State to hold the total cost

  return (
    <div className="app">
      {/* ... (remaining code remains unchanged) ... */}
      <div className="container">
        <div className="shoppingItem">
          {/* ... (remaining code remains unchanged) ... */}
        </div>
      </div>

      {/* Show the total price at the bottom */}
      <div className="totalPrice">
        <p>Total Price: {totalCost}</p>
      </div>
    </div>
  );
}

export default App;
