import "./App.css"
import React, { useState } from "react"
import productsData from "./productsData.json"

function App() {
  const shoesProducts = productsData.filter(
    (product) => product.category === "Shoes"
  )
  const watchesProducts = productsData.filter(
    (product) => product.category === "Watches"
  )

  let [displayShoes, setDisplayShoes] = useState(true)
  let [displayWatches, setDisplayWatches] = useState(false)

  const handleShoes = () => {
    setDisplayWatches(false)
    setDisplayShoes(true)
  }

  const handleWatches = () => {
    setDisplayShoes(false)
    setDisplayWatches(true)
  }

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input id="searchItem" placeholder="Search Item"></input>
        <p id="cart-logo">Cart</p>
      </div>
      <div className="container">
        <div className="offerSection"></div>
        <div className="shoppingItem">
          <button id="shoesBtn" onClick={handleShoes}>Shoes</button>
          <button id="watchesBtn" onClick={handleWatches}>Watches</button>

          {displayShoes && (
          <div className="shoes">


                shoesProducts.map((product) => (
                  <div key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p>{product.price}</p>
                    <p>{product.description}</p>
                  </div>
                ))

          </div>
          )}

{displayWatches && (
          <div className="watches">

                watchesProducts.map((product) => (
                  <div key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p>{product.price}</p>
                    <p>{product.description}</p>
                  </div>
                ))

          </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default App
