import "./App.css";
import React, { useState, useEffect, useRef } from "react";
import productsData from "./productsData.json";

function App() {
  const allProducts = productsData;
  const [displayShoes, setDisplayShoes] = useState(true);
  const [displayWatches, setDisplayWatches] = useState(false);
  const [displayAllProducts, setDisplayAllProducts] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [cartProducts, setCartProducts] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
    setDisplayAllProducts(false);
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
    setDisplayAllProducts(false);
  };

  const handleSearchItem = (event) => {
    const searchValue = event.target.value;
    setSearchTerm(searchValue);

    if (searchValue.length > 0) {
      setDisplayShoes(false);
      setDisplayWatches(false);
      setDisplayAllProducts(true);
    } else {
      setDisplayShoes(true);
      setDisplayWatches(false);
      setDisplayAllProducts(false);
    }
  };

  const filteredProducts = allProducts.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCartClick = () => {
    setShowCartPopup(!showCartPopup);
  };

  const cartPopupRef = useRef(null);

  const handleCart = (id, image, name, price, category) => {
    const existingProductIndex = cartProducts.findIndex(
      (product) => product.id === id
    );

    if (existingProductIndex !== -1) {
      // If the product already exists
      const updatedCartProducts = [...cartProducts];
      updatedCartProducts[existingProductIndex].quantity++;
      setCartProducts(updatedCartProducts);
    } else {
      const newCartItem = { id, image, name, price, category, quantity: 1 };
      setCartProducts((prevCartProducts) => [...prevCartProducts, newCartItem]);
    }
  };

  const handleRemoveFromCart = (productId) => {
    setCartProducts((prevCartProducts) =>
      prevCartProducts.filter((product) => product.id !== productId)
    );
  };

  useEffect(() => {
    const totalCost = cartProducts.reduce(
      (total, product) => total + Number(product.price) * product.quantity,
      0
    );
    setTotalCost(totalCost);

    // Remove products with quantity === 0 from the cart
    setCartProducts((prevCartProducts) =>
      prevCartProducts.filter((product) => product.quantity > 0)
    );
  }, [cartProducts]);

  const handleDecQuantity = (productId) => {
    setCartProducts((prevCartProducts) =>
      prevCartProducts.map((product) => {
        if (product.id === productId && product.quantity > 1) {
          return { ...product, quantity: product.quantity - 1 };
        }
        return product;
      })
    );
  };

  const handleIncQuantity = (productId) => {
    setCartProducts((prevCartProducts) =>
      prevCartProducts.map((product) => {
        if (product.id === productId) {
          return { ...product, quantity: product.quantity + 1 };
        }
        return product;
      })
    );
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (cartPopupRef.current && !cartPopupRef.current.contains(event.target)) {
        setShowCartPopup(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  useEffect(() => {
    // Remove products with quantity === 0 from the cart
    setCartProducts((prevCartProducts) =>
      prevCartProducts.filter((product) => product.quantity > 0)
    );

    // Recalculate the total cost after removing products with zero quantity
    const totalCost = cartProducts.reduce(
      (total, product) => total + Number(product.price) * product.quantity,
      0
    );
    setTotalCost(totalCost);
  }, [cartProducts]);

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input
          id="searchItem"
          placeholder="Search Product"
          onChange={handleSearchItem}
        />
        <div id="cart-logo" onClick={handleCartClick}>
          Cart
        </div>

        {showCartPopup && (
          <div ref={cartPopupRef} id="cart-popup" className="cart-popup">
            <div className="cart-content">
              {cartProducts.length > 0 ? (
                cartProducts.map((product) => (
                  <div key={product.id} className="cartDetails">
                    <img
                      id="cartProductImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <p id="cartProductName">{product.name}</p>
                    {product.quantity > 0 ? (
                      <div className="incDecQuantity">
                        <button
                          id="decQuantity"
                          onClick={() => handleDecQuantity(product.id)}
                          className="circleButton"
                        >
                          -
                        </button>
                        <p id="quantity">{product.quantity}</p>
                        <button
                          id="incQuantity"
                          onClick={() => handleIncQuantity(product.id)}
                          className="circleButton"
                        >
                          +
                        </button>
                      </div>
                    ) : (
                      <button
                        id="removeFromCartBtn"
                        onClick={() => handleRemoveFromCart(product.id)}
                      >
                        Remove from Cart
                      </button>
                    )}
                    <p id="cartProductPrice">Rs{product.price}</p>
                  </div>
                ))
              ) : (
                <p>No products in the cart.</p>
              )}

              {cartProducts.length > 0 && (
                <div className="totalPrice">
                  <h3>Total Price: {totalCost}</h3>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button
              id="shoesBtn"
              className={displayShoes ? "shoesActive" : ""}
              onClick={handleDisplayShoes}
            >
              Shoes
            </button>
            <button
              id="watchesBtn"
              className={displayWatches ? "watchesActive" : ""}
              onClick={handleDisplayWatches}
            >
              Watches
            </button>
          </div>

          {displayAllProducts && (
            <div className="allProducts">
              {filteredProducts.map((product) => (
                <div className="productDesc" key={product.id}>
                  <img
                    id="productImg"
                    src={product.image}
                    alt={product.name}
                  />
                  <h3>{product.name}</h3>
                  <p id="itemPrice">Rs{product.price}</p>
                  {cartProducts.some((cartProduct) => cartProduct.id === product.id) ? (
                    cartProducts.find((cartProduct) => cartProduct.id === product.id).quantity > 0 ? (
                      <div className="incDecQuantity">
                        <button
                          id="decQuantity"
                          onClick={() => handleDecQuantity(product.id)}
                          className="circleButton"
                        >
                          -
                        </button>
                        <p id="quantity">
                          {cartProducts.find((cartProduct) => cartProduct.id === product.id)?.quantity || 0}
                        </p>
                        <button
                          id="incQuantity"
                          onClick={() => handleIncQuantity(product.id)}
                          className="circleButton"
                        >
                          +
                        </button>
                      </div>
                    ) : (
                      <button
                        id="removeFromCartBtn"
                        onClick={() => handleRemoveFromCart(product.id)}
                      >
                        Remove from Cart
                      </button>
                    )
                  ) : (
                    <button
                      id="addToCartBtn"
                      onClick={() =>
                        handleCart(
                          product.id,
                          product.image,
                          product.name,
                          product.price,
                          product.category
                        )
                      }
                    >
                      Add to Cart
                    </button>
                  )}
                </div>
              ))}
            </div>
          )}

          {displayShoes && (
            <div className="shoes">
              {filteredProducts
                .filter((product) => product.category === "Shoes")
                .map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">Rs{product.price}</p>
                    {cartProducts.some((cartProduct) => cartProduct.id === product.id) ? (
                      cartProducts.find((cartProduct) => cartProduct.id === product.id).quantity > 0 ? (
                        <div className="incDecQuantity">
                          <button
                            id="decQuantity"
                            onClick={() => handleDecQuantity(product.id)}
                            className="circleButton"
                          >
                            -
                          </button>
                          <p id="quantity">
                            {cartProducts.find((cartProduct) => cartProduct.id === product.id)?.quantity || 0}
                          </p>
                          <button
                            id="incQuantity"
                            onClick={() => handleIncQuantity(product.id)}
                            className="circleButton"
                          >
                            +
                          </button>
                        </div>
                      ) : (
                        <button
                          id="removeFromCartBtn"
                          onClick={() => handleRemoveFromCart(product.id)}
                        >
                          Remove from Cart
                        </button>
                      )
                    ) : (
                      <button
                        id="addToCartBtn"
                        onClick={() =>
                          handleCart(
                            product.id,
                            product.image,
                            product.name,
                            product.price,
                            "Shoes"
                          )
                        }
                      >
                        Add to Cart
                      </button>
                    )}
                  </div>
                ))}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {filteredProducts
                .filter((product) => product.category === "Watches")
                .map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">Rs{product.price}</p>
                    {cartProducts.some((cartProduct) => cartProduct.id === product.id) ? (
                      cartProducts.find((cartProduct) => cartProduct.id === product.id).quantity > 0 ? (
                        <div className="incDecQuantity">
                          <button
                            id="decQuantity"
                            onClick={() => handleDecQuantity(product.id)}
                            className="circleButton"
                          >
                            -
                          </button>
                          <p id="quantity">
                            {cartProducts.find((cartProduct) => cartProduct.id === product.id)?.quantity || 0}
                          </p>
                          <button
                            id="incQuantity"
                            onClick={() => handleIncQuantity(product.id)}
                            className="circleButton"
                          >
                            +
                          </button>
                        </div>
                      ) : (
                        <button
                          id="removeFromCartBtn"
                          onClick={() => handleRemoveFromCart(product.id)}
                        >
                          Remove from Cart
                        </button>
                      )
                    ) : (
                      <button
                        id="addToCartBtn"
                        onClick={() =>
                          handleCart(
                            product.id,
                            product.image,
                            product.name,
                            product.price,
                            "Watches"
                          )
                        }
                      >
                        Add to Cart
                      </button>
                    )}
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
