import React, { useState, useEffect } from "react";
import productsData from "./productsData.json";

const App = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Fetch product data from JSON file (simulating an API call)
    // In a real-world scenario, you would use a proper API call.
    // For this example, we are directly importing the data from the JSON file.
    // You may need to use a relative path based on your project structure.
    // Example: import productsData from "./path/to/productsData.json";

    // Simulating a delay to mimic API response time (remove this in real code)
    const delay = setTimeout(() => {
      // Set the product data in state
      setProducts(productsData);
    }, 1000);

    return () => clearTimeout(delay);
  }, []);

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  const handleAddToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  return (
    <div>
      <h1>E-commerce Platform</h1>
      <div className="product-list">
        {productsData.map((product) => (
          <div key={product.id} className="product-item">
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <p>Category: {product.category}</p>
            <img src={product.image} alt={product.name} />
            <button onClick={() => handleProductClick(product)}>
              View Details
            </button>
            <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <h2>{selectedProduct.name}</h2>
          <p>Price: ${selectedProduct.price}</p>
          <p>Category: {selectedProduct.category}</p>
          <img src={selectedProduct.image} alt={selectedProduct.name} />
          <p>{selectedProduct.description}</p>
          <button onClick={() => handleAddToCart(selectedProduct)}>
            Add to Cart
          </button>
        </div>
      )}

      {cart.length > 0 && (
        <div className="cart">
          <h2>Cart</h2>
          <ul>
            {cart.map((product) => (
              <li key={product.id}>
                {product.name} - ${product.price}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
