import "./App.css";
import React, { useState, useEffect, useRef } from "react";
import productsData from "./productsData.json";

function App() {
  const allProducts = productsData;
  const [shoesProducts, setShoesProducts] = useState(allProducts.filter((product) => product.category === "Shoes"));
  const [watchesProducts, setWatchesProducts] = useState(allProducts.filter((product) => product.category === "Watches"));
  const [displayShoes, setDisplayShoes] = useState(true);
  const [displayWatches, setDisplayWatches] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [showCartPopup, setShowCartPopup] = useState(false);
  const [cartProducts, setCartProducts] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const handleDisplayShoes = () => {
    setDisplayWatches(false);
    setDisplayShoes(true);
  };

  const handleDisplayWatches = () => {
    setDisplayShoes(false);
    setDisplayWatches(true);
  };

  const handleSearchItem = (event) => {
    const searchValue = event.target.value;
    setSearchTerm(searchValue);
  };

  useEffect(() => {
    // Filter the products based on the search term
    const filtered = allProducts.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setShoesProducts(filtered.filter((product) => product.category === "Shoes"));
    setWatchesProducts(filtered.filter((product) => product.category === "Watches"));
  }, [searchTerm]);

  const handleCartClick = () => {
    setShowCartPopup(!showCartPopup);
  };

  const cartPopupRef = useRef(null);

  const handleCart = (id, image, name, price, category) => {
    // Check if the product already exists in the cart
    const existingProductIndex = cartProducts.findIndex(
      (product) => product.id === id
    );

    if (existingProductIndex !== -1) {
      // If the product already exists, update its quantity
      const updatedCartProducts = [...cartProducts];
      updatedCartProducts[existingProductIndex].quantity++;
      setCartProducts(updatedCartProducts);
    } else {
      // If the product is not in the cart, add it with quantity 1
      const newCartItem = { id, image, name, price, category, quantity: 1 };
      setCartProducts((prevCartProducts) => [...prevCartProducts, newCartItem]);
    }
  };

  useEffect(() => {
    // Calculate the total cost whenever cartProducts changes
    const totalCost = cartProducts.reduce(
      (total, product) => total + Number(product.price) * product.quantity,
      0
    );
    // Update the totalCost state
    setTotalCost(totalCost);
  }, [cartProducts]);

  const handleDecQuantity = (productId) => {
    setCartProducts((prevCartProducts) =>
      prevCartProducts.map((product) => {
        if (product.id === productId && product.quantity > 1) {
          return { ...product, quantity: product.quantity - 1 };
        }
        return product;
      })
    );
  };

  const handleIncQuantity = (productId) => {
    setCartProducts((prevCartProducts) =>
      prevCartProducts.map((product) => {
        if (product.id === productId) {
          return { ...product, quantity: product.quantity + 1 };
        }
        return product;
      })
    );
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (cartPopupRef.current && !cartPopupRef.current.contains(event.target)) {
        setShowCartPopup(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <div className="app">
      <div className="header">
        <p id="logo">ShopCart</p>
        <input
          id="searchItem"
          placeholder="Search Product"
          onChange={handleSearchItem}
        />
        <div id="cart-logo" onClick={handleCartClick}>
          Cart
        </div>

        {showCartPopup && (
          <div ref={cartPopupRef} id="cart-popup" className="cart-popup">
            <div className="cart-content">
              {cartProducts.length > 0 ? (
                cartProducts.map((product) => (
                  <div key={product.id} className="cartDetails">
                    <img
                      id="cartProductImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <p id="cartProductName">{product.name}</p>
                    <div className="incDecQuantity">
                      <button
                        id="decQuantity"
                        onClick={() => handleDecQuantity(product.id)}
                        className="circleButton"
                      >
                        -
                      </button>
                      <p id="quantity">{product.quantity}</p>
                      <button
                        id="incQuantity"
                        onClick={() => handleIncQuantity(product.id)}
                        className="circleButton"
                      >
                        +
                      </button>
                    </div>
                    <p id="cartProductPrice">Rs{product.price}</p>
                  </div>
                ))
              ) : (
                <p>No products in the cart.</p>
              )}

              {cartProducts.length > 0 && (
                <div className="totalPrice">
                  <h3>Total Price: {totalCost}</h3>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="container">
        <div className="shoppingItem">
          <div className="allItemsBtn">
            <button id="shoesBtn" onClick={() => setSearchTerm("")}>
              Shoes
            </button>
            <button id="watchesBtn" onClick={() => setSearchTerm("")}>
              Watches
            </button>
          </div>

          {displayShoes && (
            <div className="shoes">
              {filteredProducts
                .filter((product) => product.category === "Shoes")
                .map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">Rs{product.price}</p>
                    <button
                      id="addToCartBtn"
                      onClick={() =>
                        handleCart(
                          product.id,
                          product.image,
                          product.name,
                          product.price,
                          "Shoes"
                        )
                      }
                    >
                      Add to Cart
                    </button>
                  </div>
                ))}
            </div>
          )}

          {displayWatches && (
            <div className="watches">
              {filteredProducts
                .filter((product) => product.category === "Watches")
                .map((product) => (
                  <div className="productDesc" key={product.id}>
                    <img
                      id="productImg"
                      src={product.image}
                      alt={product.name}
                    />
                    <h3>{product.name}</h3>
                    <p id="itemPrice">Rs{product.price}</p>
                    <button
                      id="addToCartBtn"
                      onClick={() =>
                        handleCart(
                          product.id,
                          product.image,
                          product.name,
                          product.price,
                          "Watches"
                        )
                      }
                    >
                      Add to Cart
                    </button>
                  </div>
                ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
